INSERT INTO `task_756571` (`id`,`province_id`,`province_name`,`city_id`,`city_name`,`district_id`,`district_name`,`name`,`slug`,`school_type`,`class_type`,`full_name`,`master`,`accommodation`,`establishment_year`,`entrance_way`,`tel`,`test_type`,`addr`,`special_admissions`,`website`,`content`) VALUES ('149937','3111','福建','3112','福州','3118','福清市','福清市康辉中学','','公办校','','福清市康辉中学','','','','','0591-85286445','','福建省福清市音西街道音西溪前118号','','http://www.fqkhzx.cn/','<h3 class="schoolh3 cg1">福清市康辉中学学校简介</h3>                                              　　康辉中学创办于一九九一年九月，是由旅居印尼华侨蔡道行先生捐资兴建的一所公办完全中学。为福建省三级达标高中和福州市义务教育标准化学校及福清市文明学校。学校位于市区西部，区位优势明显，交通便捷。学校现有教学班48个（其中初中24个、高中24个）；学生2200人；教职工170人。校园占地面积50亩，学校基础设施齐全，有300米塑胶跑道的田径运动场，教学大楼两幢，综合实验办公楼、学生宿舍楼、教工宿舍楼和学生食堂各一幢；学校绿树掩映，布局合理，环境优美。<a class="orange_12" href="http://school.zhongkao.com/school/sxgl/149937/intro.shtml"></a>');
INSERT INTO `task_756571` (`id`,`province_id`,`province_name`,`city_id`,`city_name`,`district_id`,`district_name`,`name`,`slug`,`school_type`,`class_type`,`full_name`,`master`,`accommodation`,`establishment_year`,`entrance_way`,`tel`,`test_type`,`addr`,`special_admissions`,`website`,`content`) VALUES ('149984','3111','福建','3112','福州','3118','福清市','福清市高山中学','','公办校','','福清市高山中学','','','','','0591-85891424','','福建福清市高山镇育才路31号','','http://www.fqgszx.com/','<h3 class="schoolh3 cg1">福清市高山中学学校简介</h3>                                              　　福清高山中学（原福清五中）创办于1956年，是福清五镇最早的公立老完中之一，已经走过了55年的风雨历程。从这里，先后走出了50届13720位初中毕业生、36届8656位高中毕业生（我校毕业生档案完好无缺，学校被评为福建省档案管理先进单位）。从学校创办之时起，福清高山中学一天也没有放弃过做大做强的梦想，任何坎坷和波折，都无法阻挡她前行的脚步。2008年4月，经福建省教育厅批准，学校升格为福建省三级达标重点中学。2010年秋季，初中在校生18个班、高中在校生21个班，共2159名，教职员工163名。<a class="orange_12" href="http://school.zhongkao.com/school/sxgl/149984/intro.shtml"></a>');
