INSERT INTO `task_756571` (`id`,`province_id`,`province_name`,`city_id`,`city_name`,`district_id`,`district_name`,`name`,`slug`,`school_type`,`class_type`,`full_name`,`master`,`accommodation`,`establishment_year`,`entrance_way`,`tel`,`test_type`,`addr`,`special_admissions`,`website`,`content`) VALUES ('239622','3111','福建','3112','福州','3118','福清市','元载中学','','民办校','','元载中学','','','','','0591-85564552','','福清市海口镇前村村北30号','','http://fqyzzx.30edu.com/','<h3 class="schoolh3 cg1">元载中学学校简介</h3>                                              　　元载中学创建于1993年9月，是祖籍海口牛宅旅居印尼的著名企业家林绍良先生倾资2000万人民币创办的。草创伊始，学校暂寄前村小学旧校舍办学，1997年搬迁新校，2000年9月经福建省教育厅批复升格为完中。<a class="orange_12" href="http://school.zhongkao.com/school/sxgl/239622/intro.shtml"></a>');
