INSERT INTO `task_756571` (`id`,`province_id`,`province_name`,`city_id`,`city_name`,`district_id`,`district_name`,`name`,`slug`,`school_type`,`class_type`,`full_name`,`master`,`accommodation`,`establishment_year`,`entrance_way`,`tel`,`test_type`,`addr`,`special_admissions`,`website`,`content`) VALUES ('149598','3111','福建','3112','福州','1361','鼓楼区','福建省福州市屏东中学','','公办校','','福建省福州市屏东中学','','','','','0591-215400','','福州市鼓楼区屏东路39号','','http://www.fzpdzx.com/','<h3 class="schoolh3 cg1">福建省福州市屏东中学学校简介</h3>                                              　　福州屏东中学是省一级达标学校，她坐落于屏山东麓华林路南侧，毗邻省政府所在地，创办于1982年，是一所校龄虽短，但迅速崛起，发展稳定的学校。学校以治学严谨，校风好，环境优美，办学效益高为社会所称誉。最近该校还创办了位于屏山北面琴亭路中段（省安全厅对面）的屏北分校。<a class="orange_12" href="http://school.zhongkao.com/school/sxgl/149598/intro.shtml"></a>');
INSERT INTO `task_756571` (`id`,`province_id`,`province_name`,`city_id`,`city_name`,`district_id`,`district_name`,`name`,`slug`,`school_type`,`class_type`,`full_name`,`master`,`accommodation`,`establishment_year`,`entrance_way`,`tel`,`test_type`,`addr`,`special_admissions`,`website`,`content`) VALUES ('149590','3111','福建','3112','福州','1361','鼓楼区','福州市第十一中学','','公办校','','福州市第十一中学','','','','','0591-87553457','','福建省福州市鼓楼区劳动路18号','','http://www.fzsyz.cn/page...','<h3 class="schoolh3 cg1">福州市第十一中学学校简介</h3>                                              　　福建省福州第十一中学始建于1927年，位于福州市繁华的中心区，五一北路东侧，古仙桥畔，是一所美如花园的省二级达标中学。学校现有校园面积36.5亩，近年来新建成教学楼两座，综合实验楼、图书楼各一座。建有市属中学第一条200米塑胶跑道和600平方米雨盖体操房。校园绿化覆盖面积5600平方米。校园楼宇错落、绿树叠翠、繁花似锦。学校教学设备齐全，共有电脑200台，多媒体语音室一间，多媒体教室三间，多功能教室一间，理、化、生实验室均按Ⅰ类标准配备。图书馆已经通过一级达标验收。馆内藏书达六万余册，实行现代化电脑管理。<a class="orange_12" href="http://school.zhongkao.com/school/sxgl/149590/intro.shtml"></a>');
INSERT INTO `task_756571` (`id`,`province_id`,`province_name`,`city_id`,`city_name`,`district_id`,`district_name`,`name`,`slug`,`school_type`,`class_type`,`full_name`,`master`,`accommodation`,`establishment_year`,`entrance_way`,`tel`,`test_type`,`addr`,`special_admissions`,`website`,`content`) VALUES ('247756','1355','江苏','1356','南京','1361','鼓楼区','江苏省教育学院附属高级中学','','公办校','','江苏省教育学院附属高级中学','','','','','025-83776976','','江苏省南京市鼓楼区玉泉路1号','','http://www.nj29jt.net/','<h3 class="schoolh3 cg1">江苏省教育学院附属高级中学学校简介</h3>                                              　　江苏教育学院附属高级中学创建于1945年，始名私立石城中学，新中国成立后由人民政府接管，1956年命名为南京市第二十九中学，1960年更名为江苏教育学院附属中学，1999年学校再次更名为江苏教育学院附属高级中学。2003年8月，经南京市人民政府同意和鼓楼区人民政府批准，江苏教育学院附属高级中学组建南京二十九中教育集团，分为龙江高中部、玉泉初中部、石城初中部、凤凰初中部四个校区。学校现有155个班级、6542名学生、517名教职工，是全省办学规模最大的中学之一；高中部为江苏省四星级高中，初中部为江苏省示范初中。<a class="orange_12" href="http://school.zhongkao.com/school/sxgl/247756/intro.shtml"></a>');
