INSERT INTO `task_756571` (`id`,`province_id`,`province_name`,`city_id`,`city_name`,`district_id`,`district_name`,`name`,`slug`,`school_type`,`class_type`,`full_name`,`master`,`accommodation`,`establishment_year`,`entrance_way`,`tel`,`test_type`,`addr`,`special_admissions`,`website`,`content`) VALUES ('73296','1000','北京','1005','朝阳区','','','北京市陈经纶中学分校','','民办校','','北京市陈经纶中学分校','','','','','64703835','','北京市朝阳区望京南湖东园223号','','http://www.cjlfx.net.cn/','<h3 class="schoolh3 cg1">北京市陈经纶中学分校学校简介</h3>                                              　　1998年，根据国发96号文件精神，汪水明校长创建了这所教育体制改革初中试验校。创建初期，学校空楼一座，荒草一片。由于体改校办学经费自筹，经纶人白手起家，凭着创业者的精神，硬是将当年黄土露天、满目萧然的操场和徒有四壁的教学楼建设成为今天这绿茵环绕、一步一景、现代化教学设施一应俱全的一座花园式学校。给学生创造了学习、生活、发展的良好空间。这期间，学校的固定资产增长了6.75倍。全校从最初的8个教学班，扩充到18个、32个，最高达到44个教学班；学生从400多名到2000多名；教职员工从24名到150多名；教育教学稳步发展，办学质量逐年提高。<br>　　2010年1月，学校从体制改革试验校转为公办校。学校由原来的一个校区增加为两个校区——东湖湾校区和原南湖东园校区，办学规模进一步扩大。转公办之前，陈经纶分校可以自主招生，学生压力比较大但教学质量也很高。转为公办校之后，生源质量受到一些影响，所以2013年的中考成绩就变成了检验陈分转公办后学校实力的一个重要标准。<a class="orange_12" href="http://school.zhongkao.com/school/sxgl/73296/intro.shtml"></a>');
INSERT INTO `task_756571` (`id`,`province_id`,`province_name`,`city_id`,`city_name`,`district_id`,`district_name`,`name`,`slug`,`school_type`,`class_type`,`full_name`,`master`,`accommodation`,`establishment_year`,`entrance_way`,`tel`,`test_type`,`addr`,`special_admissions`,`website`,`content`) VALUES ('229548','1000','北京','1005','朝阳区','','','北京教育科学研究院附属实验中学','','公办校','','北京教育科学研究院附属实验中学','','','','','010-64748559','','北京市朝阳区望京新城428楼','','http://www.cyjkyfz.com','<h3 class="schoolh3 cg1">北京教育科学研究院附属实验中学学校简介</h3>                                              　　北京教育科学研究院附属实验中学座落在北京最大的城市社区——朝阳区望京地区中心。学校具有优美的校园环境，一流的现代化教学设施，雄厚的师资力量，先进的办学理念，严谨的治学态度和国际化的办学方向（具有招收外籍生的资质）。<br>　　学校的办学思想：以学生为本，关注学生的成长，提供尊重、友爱、激发学生潜能的成长空间。<br>　　学校发展的目标：让每一个学生扬起理想的风帆；让每一个学生家长享受成功的喜悦；让实验中学成为师生放飞梦想的地方。<br>　　学生发展的目标：基础扎实，学有所长；强化素质，礼仪规范；全面发展，个性优良。<a class="orange_12" href="http://school.zhongkao.com/school/sxgl/229548/intro.shtml"></a>');
