INSERT INTO `task_756571` (`id`,`province_id`,`province_name`,`city_id`,`city_name`,`district_id`,`district_name`,`name`,`slug`,`school_type`,`class_type`,`full_name`,`master`,`accommodation`,`establishment_year`,`entrance_way`,`tel`,`test_type`,`addr`,`special_admissions`,`website`,`content`) VALUES ('73159','1000','北京','1001','东城区','','','北京汇文中学','','公办校','','北京汇文中学','','','','','010-67117375','','北京市东城区培新街6号','','http://www.huiwen.edu.cn...','<h3 class="schoolh3 cg1">北京汇文中学学校简介</h3>                                              　　北京汇文中学始建于1871年，是首都百年名校。学校2005年起恢复初中部，现为包括高中部、初中部以及国际部在内的完全中学。136年的办学实践中，汇文形成了深厚的人文积淀和历史传统，培养出以彭雪枫、张克侠为代表的老一辈无产阶级革命家，和以王大珩、王忠诚、林同炎等二十多位院士为代表的著名科学家。在新中国建立后历次市级重点中学的评审中，汇文均名列其中。2001年，汇文中学成为北京市首批示范性普通高中校。<a class="orange_12" href="http://school.zhongkao.com/school/sxgl/73159/intro.shtml"></a>');
