INSERT INTO `task_756571` (`id`,`province_id`,`province_name`,`city_id`,`city_name`,`district_id`,`district_name`,`name`,`slug`,`school_type`,`class_type`,`full_name`,`master`,`accommodation`,`establishment_year`,`entrance_way`,`tel`,`test_type`,`addr`,`special_admissions`,`website`,`content`) VALUES ('73470','1000','北京','1006','海淀区','','','北京市海淀区星星学校','','','','北京市海淀区星星学校','','','','','','','','','','<h3 class="schoolh3 cg1">北京市海淀区星星学校学校简介</h3>                                    北京市海淀区星星学校于1997年3经市教委、海淀区政府、海淀教委批准成立（批准号为海政发9号），学校校长兼法人代表由民主人士、海淀区政协委员陈媛媛女士担任，执行校长由原市重点汇文中学教学校长任忠文担任，教学顾问由原人大附中校长朱迪生，特级教师孙维刚、特级教师刘梦湘、特级教师张光璐共同担任，名誉校长由君谊中学校长段君宜女士担任。');
