INSERT INTO `task_756571` (`id`,`province_id`,`province_name`,`city_id`,`city_name`,`district_id`,`district_name`,`name`,`slug`,`school_type`,`class_type`,`full_name`,`master`,`accommodation`,`establishment_year`,`entrance_way`,`tel`,`test_type`,`addr`,`special_admissions`,`website`,`content`) VALUES ('239656','3111','福建','3126','厦门','3128','海沧区','海沧中学','','公办校','','海沧中学','','','','','0592-6081223','','福建省厦门市海沧新阳新景路159号','','http://www.xmhczx.com/','<h3 class="schoolh3 cg1">海沧中学学校简介</h3>                                              　　厦门市海沧中学创办于1949年，伴随着共和国前进的步伐，至今已经走过半个多世纪。学校地处厦门市海沧区，毗邻闽台两地共仰的名胜--慈济东宫，北接集美工业区，西连闽南金三角--漳州角美开发区，东眺厦门岛，南傍九龙江。学校占地面积40000平方米，建筑面积15299平方米，办公楼、教学楼、实验楼、宿舍楼和塑胶运动场等一应俱全。学校现有绿化面积13000平方米，图书馆藏书5万多册，教学班40个，全部实现多媒体教学和网络实时教学，现有1928个学生，157位教师。<a class="orange_12" href="http://school.zhongkao.com/school/sxgl/239656/intro.shtml"></a>');
INSERT INTO `task_756571` (`id`,`province_id`,`province_name`,`city_id`,`city_name`,`district_id`,`district_name`,`name`,`slug`,`school_type`,`class_type`,`full_name`,`master`,`accommodation`,`establishment_year`,`entrance_way`,`tel`,`test_type`,`addr`,`special_admissions`,`website`,`content`) VALUES ('239653','3111','福建','3126','厦门','3127','思明区','成功学校','','民办校','','成功学校','','','','','0592-3927172','','思明区上李121号','','http://','<h3 class="schoolh3 cg1">成功学校学校简介</h3>                                              　　厦门市成功学校是一所经教育局批准设立的正规民办学校。学校设有小学部、初中部和高中部。高中部经市教育局批准，今年招收两个综合高中班，经市招办批准正式录取我校的学生参加省高中结业会考，成绩合格者按相关规定核发毕业证书，国家承认其学历，与公办校学生享有同等的参加高考或就业的资格。<a class="orange_12" href="http://school.zhongkao.com/school/sxgl/239653/intro.shtml"></a>');
INSERT INTO `task_756571` (`id`,`province_id`,`province_name`,`city_id`,`city_name`,`district_id`,`district_name`,`name`,`slug`,`school_type`,`class_type`,`full_name`,`master`,`accommodation`,`establishment_year`,`entrance_way`,`tel`,`test_type`,`addr`,`special_admissions`,`website`,`content`) VALUES ('150061','3111','福建','3126','厦门','3127','思明区','厦门交通职业中等专业学校培训中心','','公办校','','厦门交通职业中等专业学校培训中心','','','','','0592-6293005','','福建省厦门市开元区湖滨南路22号','','http://','<h3 class="schoolh3 cg1">厦门交通职业中等专业学校培训中心学校简介</h3>                                              　　为了满足经济特区建设和改革开放发展的需要，1982年9月18日，在美丽的员当湖畔，厦门市第十一中学正式成立。第二年又创办了厦门交通职业中学， 1990年4月发展成为厦门市交通职业中等专业学校，1995年6月经福建省人民政府确定为省级重点职业高级中学，2002年4月，通过福建省教委评审复评，再次被确认为省级重点职业高级中学，同时是全国交通中专教育研究理事会学校之一。<a class="orange_12" href="http://school.zhongkao.com/school/sxgl/150061/intro.shtml"></a>');
INSERT INTO `task_756571` (`id`,`province_id`,`province_name`,`city_id`,`city_name`,`district_id`,`district_name`,`name`,`slug`,`school_type`,`class_type`,`full_name`,`master`,`accommodation`,`establishment_year`,`entrance_way`,`tel`,`test_type`,`addr`,`special_admissions`,`website`,`content`) VALUES ('239651','3111','福建','3126','厦门','3127','思明区','前埔中学','','民办校','','前埔中学','','','','','0592-5929006','','厦门前思明区埔古楼南里32号','','http://','<h3 class="schoolh3 cg1">前埔中学学校简介</h3>                                              　　厦门市前埔中学，现已更名为厦门市大同中学。学校位于风景秀丽的万石植物园山麓，是一所历史悠久、校风严谨、文化底蕴深厚的名校。学校创办于1925年，是厦门市最早开办的三所中学之一。目前学校占地面积达到81亩（全区中学面积最大），教职工240人、学生3168人，一跃为思明区一所各种设施齐全的现代化完全中学。<a class="orange_12" href="http://school.zhongkao.com/school/sxgl/239651/intro.shtml"></a>');
INSERT INTO `task_756571` (`id`,`province_id`,`province_name`,`city_id`,`city_name`,`district_id`,`district_name`,`name`,`slug`,`school_type`,`class_type`,`full_name`,`master`,`accommodation`,`establishment_year`,`entrance_way`,`tel`,`test_type`,`addr`,`special_admissions`,`website`,`content`) VALUES ('239652','3111','福建','3126','厦门','3127','思明区','九中分校','','民办校','','九中分校','','','','','0592-5860091','','厦禾路870号','','http://','<h3 class="schoolh3 cg1">九中分校学校简介</h3>                                              　　厦门市第九中学创办于1973年春季，原是厦门市一所普通完全中学。2004年11月，该校被省教育厅确认为“福建省普通初中示范校”。厦门九中地处厦门市区中心，坐落于风景秀丽的金榜山北麓，现有占地面积53亩，建筑面积1.8万平方米，拥有2幢教学楼，1幢综合楼，1幢实验楼，设有专用音乐教室、美术画室、电脑室、理化生实验室、图书馆、教工俱乐部、室内体操房等。拥有250米的塑胶跑道，天然草皮的足球场，还有多个篮排球场、乒乓球台。校园内部布局合理，设备功能齐全，树木苍翠，花草葱茏，环境优美。学校现有33个教学班，在校生1375人。<a class="orange_12" href="http://school.zhongkao.com/school/sxgl/239652/intro.shtml"></a>');
INSERT INTO `task_756571` (`id`,`province_id`,`province_name`,`city_id`,`city_name`,`district_id`,`district_name`,`name`,`slug`,`school_type`,`class_type`,`full_name`,`master`,`accommodation`,`establishment_year`,`entrance_way`,`tel`,`test_type`,`addr`,`special_admissions`,`website`,`content`) VALUES ('239661','3111','福建','3126','厦门','3130','集美区','集美中学','','民办校','','集美中学','','','','','0592-6105005','','厦门集美鳌园路19号','','http://jmzx.xmedu.cn/','<h3 class="schoolh3 cg1">集美中学学校简介</h3>                                              　　厦门集美中学系爱国华侨领袖陈嘉庚先生1918年10月18日创办的，历史悠久，业绩辉煌，学子遍五洲，声名播四海，被誉为“中国名校”。系福建省首批办好的重点中学、省一级达标学校、省文明单位、省素质教育先进校、省德育基地、省校园安全先进校。<br>　　二十世纪初叶，被毛泽东主席称为“华侨旗帜，民族光辉”的陈嘉庚先生针对中国内忧外患，贫穷落后的状况，立下兴学报国之志。他在海外艰苦创业，倾尽毕生资财和心血，创办了厦门大学，创办了以集美中学为主的闻名中外的集美学校，并立下“诚毅”校训。建校以来集美中学培养和造就了许多卓有成就的人才，如两院院士伍献文、蔡启瑞、张乾二、邓从豪、李幼平，著名作家白刃，著名电影导演黄健中，著名作曲家李海晖，著名书画家梁披云，黄永玉，著名实业家李尚大、李陆大等等。学校被称为“革命摇篮”，有着光荣的爱国革命传统，抗日民族女英雄李林校友就是其中杰出代表。学校被称为“侨生摇篮”，培养的5万多名校友，相当一部分分布在海外，成名成家成才，为人类发展和社会进步作出积极贡献。郭沫若先生有联赞曰：“鳌园博物大观百闻不如一见，鹭江集美中学万人共仰千秋”。<a class="orange_12" href="http://school.zhongkao.com/school/sxgl/239661/intro.shtml"></a>');
INSERT INTO `task_756571` (`id`,`province_id`,`province_name`,`city_id`,`city_name`,`district_id`,`district_name`,`name`,`slug`,`school_type`,`class_type`,`full_name`,`master`,`accommodation`,`establishment_year`,`entrance_way`,`tel`,`test_type`,`addr`,`special_admissions`,`website`,`content`) VALUES ('150054','3111','福建','3126','厦门','3127','思明区','福建省厦门双十中学','','公办校','','福建省厦门双十中学','','','','','0592-2117888','','福建省厦门市镇海路33号','','http://www.sszx.cn/','<h3 class="schoolh3 cg1">福建省厦门双十中学学校简介</h3>                                              　　厦门双十中学创办于1919年。校庆日定为10月10日，取名“双十”，是为了纪念辛亥革命。创办人是爱国华侨马侨儒先生。最早的校址在霞溪路口，校名是厦门双十乙种商业学校，马侨儒先生为首任校长。1923年菲律宾华侨林珠光捐巨资买下箭场地皮，学校迁至鸿山北麓，即现在镇海校区所在地。1925年正式更名为厦门双十中学。<a class="orange_12" href="http://school.zhongkao.com/school/sxgl/150054/intro.shtml"></a>');
INSERT INTO `task_756571` (`id`,`province_id`,`province_name`,`city_id`,`city_name`,`district_id`,`district_name`,`name`,`slug`,`school_type`,`class_type`,`full_name`,`master`,`accommodation`,`establishment_year`,`entrance_way`,`tel`,`test_type`,`addr`,`special_admissions`,`website`,`content`) VALUES ('150053','3111','福建','3126','厦门','3127','思明区','福建省厦门第一中学','','公办校','','福建省厦门第一中学','','','','','','','福建省厦门市文园路93号','','http://www.yizhong.xm.fj...','<h3 class="schoolh3 cg1">福建省厦门第一中学学校简介</h3>                                              　　福建省厦门第一中学，简称厦门一中，创办于1906年，系福建省首批办好的重点中学暨福建省普通中学一级达标学校，是福建省省级文明单位。1906年称厦门中学堂。厦门中学堂前身是玉屏书院，建于1751年，是厦门最早的中学。1912年改名思明中学。1917年春收归省立，称省立十三中。1929年改称省立厦门中学（简称省中）。1937年秋停办，1946年9月复办，厦门市立中学（简称市中），创立于1946年3月。解放后，人民政府接管了这两所学校。1951年2月省中和市中合并，称厦门第一中学。1953年定为省重点中学。并选定厦门市区东部白鹤岭南坡建筑新校舍。<a class="orange_12" href="http://school.zhongkao.com/school/sxgl/150053/intro.shtml"></a>');
