INSERT INTO `task_756571` (`id`,`province_id`,`province_name`,`city_id`,`city_name`,`district_id`,`district_name`,`name`,`slug`,`school_type`,`class_type`,`full_name`,`master`,`accommodation`,`establishment_year`,`entrance_way`,`tel`,`test_type`,`addr`,`special_admissions`,`website`,`content`) VALUES ('150608','3111','福建','3152','泉州','3156','泉港区','福建省泉州市泉港区三朱中学','','公办校','','福建省泉州市泉港区三朱中学','','','','','0595-87965158','','福建省泉州市泉港区前黄镇三朱村','','http://qgszzx.30edu.com/','<h3 class="schoolh3 cg1">福建省泉州市泉港区三朱中学学校简介</h3>                                              　　12月18日，泉港三朱中学中层干部和部分骨干教师在校长的带领下到南安市东田中学考察、研讨交流。考察团一行认真听取了东田中学林万场校长的《用文化浸润人生，以信心点亮未来》报告，以及教育教学管理改革方面的新思路与新举措。随后，参观了校园环境、学生宿舍和师生食堂，随机进行课堂观摩，并就共同关心的话题进行积极而有建设性的探讨。考察团成员深刻感受到学校教师的团队精神和敬业态度、浓厚的校园文化氛围以及科学的管理机制，尤其是校园文化建设和文明礼仪教育富有成效，大家思考着如何将这些先进的教育教学理念有效转化为符合学校发展实际的具体行动，当日参与交流的还有来自福州、厦门和洛江的三所中学。<a class="orange_12" href="http://school.zhongkao.com/school/sxgl/150608/intro.shtml"></a>');
