INSERT INTO `task_756571` (`id`,`province_id`,`province_name`,`city_id`,`city_name`,`district_id`,`district_name`,`name`,`slug`,`school_type`,`class_type`,`full_name`,`master`,`accommodation`,`establishment_year`,`entrance_way`,`tel`,`test_type`,`addr`,`special_admissions`,`website`,`content`) VALUES ('221554','3111','福建','3152','泉州','3153','鲤城区','泉州培元中学','','民办校','','泉州培元中学','','','','','0595-22383362','','福建省泉州市鲤城区新华北路花棚下','','http://www.qzpy.com/','<h3 class="schoolh3 cg1">泉州培元中学学校简介</h3>                                              　　泉州培元中学，创办于1904年，办学初期，曾得到中国革命先行者孙中山先生的关怀，1920年1月，孙中山先生带头向培元中学捐款，并亲题“协兴教育”于捐册扉页；1920年11月5日，亲笔题写“共进大同”赠勉培元中学。1980年5月，国家名誉主席宋庆龄又亲笔题写“为国树人”赠勉培元中学。<a class="orange_12" href="http://school.zhongkao.com/school/sxgl/221554/intro.shtml"></a>');
INSERT INTO `task_756571` (`id`,`province_id`,`province_name`,`city_id`,`city_name`,`district_id`,`district_name`,`name`,`slug`,`school_type`,`class_type`,`full_name`,`master`,`accommodation`,`establishment_year`,`entrance_way`,`tel`,`test_type`,`addr`,`special_admissions`,`website`,`content`) VALUES ('239523','3111','福建','3112','福州','','','福州延安中学','','公办校','普通班','福州延安中学','王金石','走读','1972年','直升','0591-87511447','分班考试题 期末试题','福州市鼓楼区学院前巷2号','','http://www.fzyazx.com.cn...','<h3 class="schoolh3 cg1">福州延安中学学校简介</h3>                                              　　福州延安中学坐落于市中心南门兜附近，面朝巍峨孔庙，背枕幽深古巷。创办于1927年，前身是我国著名的教育家、翻译家、曾获美国哥伦比亚大学博士学位的钟道赞先生在鼓楼三民里创办的省立福州职业学校，此后，学校曾易名为省立福州家事学校（1936）、省立女中（1947）、市一女中（1952）至1969年5月方定名为福州延安中学，现为福州市鼓楼区属（公立）完全中学。<a class="orange_12" href="http://school.zhongkao.com/school/sxgl/239523/intro.shtml"></a>');
