INSERT INTO `task_756571` (`id`,`province_id`,`province_name`,`city_id`,`city_name`,`district_id`,`district_name`,`name`,`slug`,`school_type`,`class_type`,`full_name`,`master`,`accommodation`,`establishment_year`,`entrance_way`,`tel`,`test_type`,`addr`,`special_admissions`,`website`,`content`) VALUES ('150934','3111','福建','3152','泉州','3163','德化县','德化县汤头中学','','公办校','','德化县汤头中学','','','','','0595-23555246','','福建省泉州市德化县汤头乡汤头村','','http://','<h3 class="schoolh3 cg1">德化县汤头中学学校简介</h3>                                              　　德化汤头中学位子汤头乡汤头村，四面青山环抱，环境清静优美，风景宜人。<br>　　学校创办于1969年，当时只有一个班级，三十多位学生。学校与汤头小学在一起，位于现在汤头村的红头坪。1972年学校搬到现中学所在地，此时学校有两个年段共计工皿多人，校舍是土木结构，有教室、师生宿舍楼、厨房及厕所各一座。随着形势的发展，一九七六年在岭脚、格中各增一所附设初中。一九七七年开设高中部，招生61人，七九年秋首届高中毕业生在高考中取得了优异的成绩。一九八零年秋，岭脚、格中附中并入汤头中学，同时由于办学条件较差，师资短缺，高中部停办。后来，随着办学规模的不断扩大，在校领导的不懈努力及上级有关部门的大力支持下，86年新建一座钢筋混凝土结构的教学楼——“翰墨楼”，95年建一座教学综合楼，96年又新建～座厨房及学生宿舍综合楼，这样学校的办学条件得到了较大的改善。<a class="orange_12" href="http://school.zhongkao.com/school/sxgl/150934/intro.shtml"></a>');
