INSERT INTO `task_756571` (`id`,`province_id`,`province_name`,`city_id`,`city_name`,`district_id`,`district_name`,`name`,`slug`,`school_type`,`class_type`,`full_name`,`master`,`accommodation`,`establishment_year`,`entrance_way`,`tel`,`test_type`,`addr`,`special_admissions`,`website`,`content`) VALUES ('150669','3111','福建','3152','泉州','3160','惠安县','惠安县侯卿中学','','公办校','','惠安县侯卿中学','','','','','0595-226400','','福建省泉州市惠安县','','http://www.hahqzx.cn/','<h3 class="schoolh3 cg1">惠安县侯卿中学学校简介</h3>                                              　　侯卿中学位于惠安县辋川镇西部，面向县城，东距福厦公路仅600米，学校创办于1993年。整个学校教学楼、办公楼、宿舍楼整然有序，环境优美，是县“优化校园育人环境十佳学校”。十几年来，学校狠抓师资队伍建设，创建名师建设工程，抓继续教育，引导行动研究，抓师风师德，培养一大批要教会教、会教要教的年轻教师。提倡成功教育，施行愉快学习。几年来，无论是综合比率还是优秀率均位于全县农村中学前列。2005年5月，我校又顺利通过了泉州市普通初中达标校评估验收。<a class="orange_12" href="http://school.zhongkao.com/school/sxgl/150669/intro.shtml"></a>');
INSERT INTO `task_756571` (`id`,`province_id`,`province_name`,`city_id`,`city_name`,`district_id`,`district_name`,`name`,`slug`,`school_type`,`class_type`,`full_name`,`master`,`accommodation`,`establishment_year`,`entrance_way`,`tel`,`test_type`,`addr`,`special_admissions`,`website`,`content`) VALUES ('222220','3111','福建','3165','漳州','3172','漳浦县','漳浦第四中学','','公办校','','漳浦第四中学','','','','','0596-3881234','','福建省漳浦县杜浔镇青竹山','','http://www.fjzzjy.gov.cn...','<h3 class="schoolh3 cg1">漳浦第四中学学校简介</h3>                                              　　五十年的风雨兼程，五十年的春华秋实。昨天（9月28日）上午，漳浦第四中学举行隆重的庆典仪式，欢庆学校五十华诞。漳州市政协主席林殿阁，谢毅泰、蔡金海、杨国华、林五四、何德发等市县领导，部分历届漳浦四中领导和教师，以及来自深圳、厦门、漳州等地的四中校友，数千名在校师生参加了庆祝活动。庆祝活动由副县长林仲文主持，庄严的国歌拉开了整场活动的序幕。<br>　　漳浦第四中学成立于1956年，校名历经&quot;漳浦县杜浔中心小学附设初中班&quot;、&quot;漳浦第四中学&quot;、&quot;杜浔人民公社工农中学&quot;、&quot;漳浦县杜浔中学&quot;等数次变更，1980年再次确定为&quot;漳浦第四中学&quot;。50年来，漳浦四中认真贯彻党的教育方针，坚持德智体全面发展，广大教师教书育人，辛勤耕耘，学校各项事业得到长足的发展，教育教学质量稳步提高，办学条件日益完善。学校现有教职员工200多名，在校学生3300多名。建校以来，已培养出二万二千多名合格的毕业生，为国家输送了大量优秀人才。如今，漳浦四中的校友遍及海内外，他们在各自工作岗位上，为祖国的建设奉献着自己的聪明才智，为母校漳浦四中增光添彩。庆典仪式上，市政协主席林殿阁、县委副书记林五四分别代表市、县两级对漳浦四中五十华诞表示祝贺，希望漳浦四中借50年校庆的强劲东风，为我县乃至国家的经济和社会发展培育出更多的栋梁之才。<a class="orange_12" href="http://school.zhongkao.com/school/sxgl/222220/intro.shtml"></a>');
